INSERT INTO CAPS.AFCARS_ELEMENT_HELP (TXT_AFCARS_ELEMENT_SHORT, NBR_AFCARS_ORDER,TXT_AFCARS_ELEMENT,TXT_AFCARS_ELEMENT_LABEL,CD_AFCARS_DATA_TYPE,TXT_AFCARS_CODES_TABLE,TXT_AFCARS_SOURCE_TEXT,TXT_AFCARS_ELEMENT_HELP_TEXT) VALUES ('AFCARS_FC_15',19,'OTHER_DIAGNOSED_CONDITION','Has the Child Been Diagnosed with any Other Medical Condition','C','CYESNOA','<a href="javascript:caseWatchNavigation(''navPersonChar'')">Person Characteristics</a>','For this to be yes for the foster care child one or more of the following must have been a current person characteristic at the time of report generation:<br>Infant Alcohol Addition, Infant Drug Addiction/prenatal Drug Exposed, Cognitive Disorder, Intellectual Disability, Learning Disability, Speech Disability, Abnormal Bowel Movement Behavior, AIDS, Asperger''s Disorder, Asthma, Cancer, Diabetes, Eczema, Epilepsy, Hepatitis, HIV Positive, Other Medically Diagnosed, Rheumatic Fever, Heart Disease, Heart Murmur, Severe Allergies, Sexual Transmitted Disease, Sickle Cell Anemia, Terminal Illness, Traumatic Brain Injury, or Tuberculosis.');
