INSERT INTO CAPS.AFCARS_ELEMENT_HELP (TXT_AFCARS_ELEMENT_SHORT, NBR_AFCARS_ORDER,TXT_AFCARS_ELEMENT,TXT_AFCARS_ELEMENT_LABEL,CD_AFCARS_DATA_TYPE,TXT_AFCARS_CODES_TABLE,TXT_AFCARS_SOURCE_TEXT,TXT_AFCARS_ELEMENT_HELP_TEXT) VALUES ('AFCARS_FC_19',23,'TOTAL_NUMBER_OF_REMOVALS','Total Number of Removals From Home to Date','T','','Prior Case History','This field is calculated based on data in previous cases: it is a count of all removals in SHINES as recorded using the custody page, plus additional converted removals that may not have a Custody page in SHINES.');
INSERT INTO CAPS.AFCARS_ELEMENT_HELP (TXT_AFCARS_ELEMENT_SHORT, NBR_AFCARS_ORDER,TXT_AFCARS_ELEMENT,TXT_AFCARS_ELEMENT_LABEL,CD_AFCARS_DATA_TYPE,TXT_AFCARS_CODES_TABLE,TXT_AFCARS_SOURCE_TEXT,TXT_AFCARS_ELEMENT_HELP_TEXT) VALUES ('AFCARS_FC_20',24,'LAST_FOSTER_CARE_DISCHARGE','Date of Last Foster Care Discharge','D','','Prior Case History','The date the child was discharged from the last foster care episode immediately prior to the current episode determined using the most recent legal status date where the child has a legal status of "not in custody" immediately following an "in custody" legal status and the "not in custody" legal status occurred prior to the current removal, or the trial home visit discharge date on Placement when the most recent previously approved placement is a trial home visit  that exceeds the court ordered end date.  Note that in some cases this may be populated with converted data not associated to a SHINES case record.');
