INSERT INTO CAPS.AFCARS_ELEMENT_HELP (TXT_AFCARS_ELEMENT_SHORT, NBR_AFCARS_ORDER,TXT_AFCARS_ELEMENT,TXT_AFCARS_ELEMENT_LABEL,CD_AFCARS_DATA_TYPE,TXT_AFCARS_CODES_TABLE,TXT_AFCARS_SOURCE_TEXT,TXT_AFCARS_ELEMENT_HELP_TEXT) VALUES ('AFCARS_FC_23',27,'DATE_OF_CURRENT_PLACEMENT','Date of Placement in Current Foster Care Setting','D','','<a href="javascript:caseWatchNavigation(''navPlacement'')">Placement Information</a>','This is populated  Placement Start Date from the Placement Information page  for the most recent placement event where the placement event is actual and not a temporary placement of type concurrent, respite day, or respite night. ');
INSERT INTO CAPS.AFCARS_ELEMENT_HELP (TXT_AFCARS_ELEMENT_SHORT, NBR_AFCARS_ORDER,TXT_AFCARS_ELEMENT,TXT_AFCARS_ELEMENT_LABEL,CD_AFCARS_DATA_TYPE,TXT_AFCARS_CODES_TABLE,TXT_AFCARS_SOURCE_TEXT,TXT_AFCARS_ELEMENT_HELP_TEXT) VALUES ('AFCARS_FC_24',28,'NUMBER_PLACEMENT_SETTINGS','Number of Previous Placement Settings During This Episode','T','','<a href="javascript:caseWatchNavigation(''navPlacementList'')">Placement List</a>','This is populated with a count of placement events which have occurred since the most recent removal under the following conditions so long as: <br>The placement is an actual, as opposed to attempted, not respite, concurrent, parental, or runaway, and not within the set of AFCARS rules to prevent double counting placements (such as returning to a former placement from a hospital).  For cases which have long been open, count of moves occurring before SHINES go live comes directly from a converted data source.');
INSERT INTO CAPS.AFCARS_ELEMENT_HELP (TXT_AFCARS_ELEMENT_SHORT, NBR_AFCARS_ORDER,TXT_AFCARS_ELEMENT,TXT_AFCARS_ELEMENT_LABEL,CD_AFCARS_DATA_TYPE,TXT_AFCARS_CODES_TABLE,TXT_AFCARS_SOURCE_TEXT,TXT_AFCARS_ELEMENT_HELP_TEXT) VALUES ('AFCARS_FC_25',29,'MANNER_OF_REMOVAL','Manner of Removal From Home for Current Placement Episode','C','CMANRMV','<a href="javascript:caseWatchNavigation(''navCustody'')">Custody Page</a>','For the most recent Custody event, If voluntary surrender or voluntary placement is marked as the removal reason AFCARS will report voluntary, otherwise it will report court ordered.  If necessary this must be corrected on Custody page, not using Legal Action or Legal Status');
