INSERT INTO CAPS.AFCARS_ELEMENT_HELP (TXT_AFCARS_ELEMENT_SHORT, NBR_AFCARS_ORDER,TXT_AFCARS_ELEMENT,TXT_AFCARS_ELEMENT_LABEL,CD_AFCARS_DATA_TYPE,TXT_AFCARS_CODES_TABLE,TXT_AFCARS_SOURCE_TEXT,TXT_AFCARS_ELEMENT_HELP_TEXT) VALUES ('AFCARS_FC_27',31,'SEXUAL_ABUSE','Sexual Abuse','C','CAPPLY','<a href="javascript:caseWatchNavigation(''navCustody'')">Custody Page</a>','This applies if Sexual Abuse/Risk was chosen as a removal reason on the current Custody event for the child.');
