INSERT INTO CAPS.AFCARS_ELEMENT_HELP (TXT_AFCARS_ELEMENT_SHORT, NBR_AFCARS_ORDER,TXT_AFCARS_ELEMENT,TXT_AFCARS_ELEMENT_LABEL,CD_AFCARS_DATA_TYPE,TXT_AFCARS_CODES_TABLE,TXT_AFCARS_SOURCE_TEXT,TXT_AFCARS_ELEMENT_HELP_TEXT) VALUES ('AFCARS_FC_31',35,'ALCOHOL_ABUSE_CHILD','Alcohol Abuse(Child)','C','CAPPLY','<a href="javascript:caseWatchNavigation(''navCustody'')">Custody Page</a>','This applies if Alcohol abuse was chosen as a child removal characteristic on the current Custody event for the child.  For this to occur Alcohol Abuse must have first been identified as a Person Characteristic for the child.');
INSERT INTO CAPS.AFCARS_ELEMENT_HELP (TXT_AFCARS_ELEMENT_SHORT, NBR_AFCARS_ORDER,TXT_AFCARS_ELEMENT,TXT_AFCARS_ELEMENT_LABEL,CD_AFCARS_DATA_TYPE,TXT_AFCARS_CODES_TABLE,TXT_AFCARS_SOURCE_TEXT,TXT_AFCARS_ELEMENT_HELP_TEXT) VALUES ('AFCARS_FC_32',36,'DRUG_ABUSE_CHILD','Drug Abuse(Child)','C','CAPPLY','<a href="javascript:caseWatchNavigation(''navCustody'')">Custody Page</a>','This applies if Drug abuse was chosen as a child removal characteristic on the current Custody event for the child.  For this to occur Drug Abuse must have first been identified as a Person Characteristic for the child.');
