INSERT INTO CAPS.AFCARS_ELEMENT_HELP (TXT_AFCARS_ELEMENT_SHORT, NBR_AFCARS_ORDER,TXT_AFCARS_ELEMENT,TXT_AFCARS_ELEMENT_LABEL,CD_AFCARS_DATA_TYPE,TXT_AFCARS_CODES_TABLE,TXT_AFCARS_SOURCE_TEXT,TXT_AFCARS_ELEMENT_HELP_TEXT) VALUES ('AFCARS_FC_56',70,'DATE_OF_DISCHARGE','Date of Discharge from Foster Care','D','','<a href="javascript:caseWatchNavigation(''navLegalStatDetail'')">Legal Status</a>','The date the child was discharged from the current episode will be reported as: <br>1) The earliest legal status date where the child has a legal status of "not in custody" immediately following an "in custody" legal status and the "not in custody" legal status occurred after the current removal. <br><br>2) The Trial Home Visit Discharge Date, when the most recent previously approved placement is a trial home visit  that exceeds the court ordered end date. <br><br>3) The child''s 18th birthday if the child turned 18 years old during the reporting period and checks on 1 and 2 are not found.');
INSERT INTO CAPS.AFCARS_ELEMENT_HELP (TXT_AFCARS_ELEMENT_SHORT, NBR_AFCARS_ORDER,TXT_AFCARS_ELEMENT,TXT_AFCARS_ELEMENT_LABEL,CD_AFCARS_DATA_TYPE,TXT_AFCARS_CODES_TABLE,TXT_AFCARS_SOURCE_TEXT,TXT_AFCARS_ELEMENT_HELP_TEXT) VALUES ('AFCARS_FC_57',71,'DISCHARGE_TRANSACTION_DATE','Date of Discharge from Foster Care Transaction Date','D','','System Generated','This system generated date can be seen on the Event List for the Legal Status event that updates the child''s legal status from in custody to not in custody.  It is recorded at the time the Legal Status is actually entered into the system and cannot be modified.');
INSERT INTO CAPS.AFCARS_ELEMENT_HELP (TXT_AFCARS_ELEMENT_SHORT, NBR_AFCARS_ORDER,TXT_AFCARS_ELEMENT,TXT_AFCARS_ELEMENT_LABEL,CD_AFCARS_DATA_TYPE,TXT_AFCARS_CODES_TABLE,TXT_AFCARS_SOURCE_TEXT,TXT_AFCARS_ELEMENT_HELP_TEXT) VALUES ('AFCARS_FC_58',72,'REASON_FOR_DISCHARGE','Reason for Discharge','C','CDISCHRA','<a href="javascript:caseWatchNavigation(''navLegalStatDetail'')">Legal Status</a>','The reason for discharge is primarily derived from the Legal Status selected on the Legal Status event that triggers the discharge date above.   These are mapped below: <br>Aftercare/Supervision: Reunification with Parents/Primary Caretakers <br>Not In DFCS Custody Child Turned 18 (No ILP): Emancipation <br>Not in DFCS Custody Child Death: Death of Child <br>Not In DFCS Custody - Custody To Other: Transfer to Another Agency <br>Not In DFCS Custody - Custody Transferred To Tribe: Transfer to Another Agency <br>Not In DFCS Custody Emancipated: Emancipation <br>Not In DFCS Custody Guardianship: Guardianship <br>Not In DFCS Custody Parental Custody: Reunification with Parents/Primary Caretakers <br>Not in DFCS Custody - Custody to Relative: Living with Relatives <br>Committed To DJJ: Transfer to Another Agency <br>ILP Aftercare: Emancipation <br><br>If the child turned 18 without updated legal status, the reason will default to Emancipation.  If the child is discharged due to a trial home visit over the timeframe, the reason will default to Reunification.');
