INSERT INTO CAPS.NCANDS_ELEMENT_HELP (TXT_NCANDS_ELEMENT_SHORT,NBR_NCANDS_ORDER,TXT_NCANDS_ELEMENT,IND_NCANDS_ELEMENT_ACTIVE,TXT_NCANDS_ELEMENT_LABEL,CD_NCANDS_DATA_TYPE,TXT_NCANDS_CODES_TABLE,TXT_NCANDS_SOURCE_TEXT,TXT_NCANDS_ELEMENT_HELP_TEXT) VALUES ('NCANDS_9',9,'RPTDISP','Y','Report Disposition','C','CRPTDISP','<a href="javascript:caseWatchNavigation(''navInvConclusion'')">Investigation Conclusion</a>','<b>Report Disposition</b><br>If any allegation in the INV stage is recorded as substantiated NCANDS reports substantiated.  If all allegations are unsubstantiated NCANDS reports unsubstantiated other than intentionally false reporting.  If all allegations are marked unable to determine NCANDS reports Other.<br>The overall substantiation of the case is displayed on Investigation Conclusion using similar rules.');
