INSERT INTO CAPS.NCANDS_ELEMENT_HELP (TXT_NCANDS_ELEMENT_SHORT,NBR_NCANDS_ORDER,TXT_NCANDS_ELEMENT,IND_NCANDS_ELEMENT_ACTIVE,TXT_NCANDS_ELEMENT_LABEL,CD_NCANDS_DATA_TYPE,TXT_NCANDS_CODES_TABLE,TXT_NCANDS_SOURCE_TEXT,TXT_NCANDS_ELEMENT_HELP_TEXT) VALUES ('NCANDS_23',23,'CHILD_LVNG','Y','Living Arrangement','C','CLVGARRN','<a href="javascript:caseWatchNavigation(''navPersonList'')">Person Detail</a>','<b>Living Arrangement</b><br>NCANDS reports the living arrangement of the child at the time of the incident as follows:<br><br>If the person with a role of Primary Caretaker is indicated as Married: Married two parent household, with two biological/adoptive parents.<br><br>If the person with a role of Primary Caretaker is indicated as Single, Separated, Widowed, or Divorced, and the Primary Caretaker is female: Single parent household, mother only.<br><br>Same as above and the Primary Caretaker is male: Single parent household, father only.<br><br>Living Arrangement at investigation of Relative Home, Relative FH, Relative Custody Placement, or Foster Care Relative: Non parent relative caretaker.<br><br>Living Arrangement at investigation of DFCS Adoptive Home, DFCS Family Foster Home, Non-DFCS Adoptive Home, Non-DFCS Family Foster Home, DJJ Contract Home, MHMR Foster Home, With Non-Related Person,Non-Related Guardian Home, Unrelated Persons, or Foster Care Non-Relative: Non-Relative Caregiver Household.<br><br>Living Arrangement at investigation of DJJ Group Home, DJJ Residential program, Group Home/Institution-under DFCS Supervision,Group Home/Institution-no DFCS Supervision, MHMR Group Home,Adult Residential Treatment Center, Sub Abs Treatment Center: Group Home or Residential Treatment Setting.<br><br>If the child has any other entered living arrangement: Other. ');
INSERT INTO CAPS.NCANDS_ELEMENT_HELP (TXT_NCANDS_ELEMENT_SHORT,NBR_NCANDS_ORDER,TXT_NCANDS_ELEMENT,IND_NCANDS_ELEMENT_ACTIVE,TXT_NCANDS_ELEMENT_LABEL,CD_NCANDS_DATA_TYPE,TXT_NCANDS_CODES_TABLE,TXT_NCANDS_SOURCE_TEXT,TXT_NCANDS_ELEMENT_HELP_TEXT) VALUES ('NCANDS_24',24,'CHILD_MIL','Y','Military Family Member','C','CYESNON','<a href="javascript:caseWatchNavigation(''navPersonChar'')">Person Detail - Person Characteristics</a>','<b>Military Family Member</b><br>If the characteristic Military Dependent - legal dependent of individual on active duty in U.S. Armed Forces is selected for the reporting child NCANDS reports yes, it otherwise reports no.  The characteristic must start before the Investigation stage closure.');
INSERT INTO CAPS.NCANDS_ELEMENT_HELP (TXT_NCANDS_ELEMENT_SHORT,NBR_NCANDS_ORDER,TXT_NCANDS_ELEMENT,IND_NCANDS_ELEMENT_ACTIVE,TXT_NCANDS_ELEMENT_LABEL,CD_NCANDS_DATA_TYPE,TXT_NCANDS_CODES_TABLE,TXT_NCANDS_SOURCE_TEXT,TXT_NCANDS_ELEMENT_HELP_TEXT) VALUES ('NCANDS_25',25,'CHILD_PRIOR','Y','Prior Victim','C','CYESNON','Prior Case History','<b>Prior Victim</b><br>Going forward from SHINES go live, if the ID of the current child matches an victim in a prior Allegation that was substantiated, NCANDS reports yes.  Otherwise NCANDS reports Unknown since all past history was not converted.');
INSERT INTO CAPS.NCANDS_ELEMENT_HELP (TXT_NCANDS_ELEMENT_SHORT,NBR_NCANDS_ORDER,TXT_NCANDS_ELEMENT,IND_NCANDS_ELEMENT_ACTIVE,TXT_NCANDS_ELEMENT_LABEL,CD_NCANDS_DATA_TYPE,TXT_NCANDS_CODES_TABLE,TXT_NCANDS_SOURCE_TEXT,TXT_NCANDS_ELEMENT_HELP_TEXT) VALUES ('NCANDS_26',26,'CHILD_MAL_1','Y','Maltreatment-1 Type','C','CMALTRTY','<a href="javascript:caseWatchNavigation(''navAllegationList'')">Allegation Detail</a>','<b>Maltreatment-1 Type</b><br>Maltreatment Type is determined from the SHINES Maltreatment Code as follows:<br><br> ''''1'''' Physical Abuse = Maltreatment Codes:<br>Fractures-Dislocations-Sprains (P1), Intercranial Skull Injury (P2), Spinal Cord/Nerve Damage (P3), Subdural Hematoma (P4), Internal Chest/Abdomen/Pelvic Injury (P5), Lacerations/Cuts/Punctures (P6), Abrasions (P7), Burns-Scalding (P8), Poisoning (P9), Suffocation/Drowning (P10), Munchausen''s (P11), Gunshot (P12), Corporal Punishment (P13)<br><br>''''2'''' Neglect (non medical) = Maltreatment Codes:<br>Malnourishment/Failure to Thrive (N1), Abandonment/Rejection (N2), Inadequate Supervision (N3), Inadequate Food/Clothing/Shelter (N4), Educational/Cognitive Neglect (N7), Gunshot (N8), Suffocation/Drowning (N9), Birth Addicted/Birth Exposed (N10)<br><br>''''3'''' Neglect (medical) = Maltreatment Code:<br>Inadequate Health/Medical Care (N5)<br><br> ''''4'''' Sexual Abuse = Maltreatment Codes:<br>Exhibitionism/Voyeurism (S1), Fondling (S2), Sodomy (S3), Penetration (S4), Genital Injury (S5), Contraction of Venereal Disease (S6), Sexual Exploitation (S7)<br><br>''''5'''' Emotional = Maltreatment Codes:<br>Emotional/Psychological Neglect (N6), Verbal Threats/Abuse (E1), Bizarre Discipline (Non-Physical) (E2), Domestic Violence (E3)<br><br>''''6'''' No Alleged Maltreatment<br>non-maltreated household members picked up in the overall population<br><br''>''8'''' Other = Maltreatment Codes:<br>Case Opened, No Maltreatment Reported (O1)');
INSERT INTO CAPS.NCANDS_ELEMENT_HELP (TXT_NCANDS_ELEMENT_SHORT,NBR_NCANDS_ORDER,TXT_NCANDS_ELEMENT,IND_NCANDS_ELEMENT_ACTIVE,TXT_NCANDS_ELEMENT_LABEL,CD_NCANDS_DATA_TYPE,TXT_NCANDS_CODES_TABLE,TXT_NCANDS_SOURCE_TEXT,TXT_NCANDS_ELEMENT_HELP_TEXT) VALUES ('NCANDS_27',27,'CHILD_MALDISP_1','Y','Maltreatment-1 Disposition Level','C','CMALDISP','<a href="javascript:caseWatchNavigation(''navAllegationList'')">Allegation Detail</a>','<b>Maltreatment-1 Disposition Level</b><br>If the allegation is Substantiated, NCANDS supports Substantiated, if Unsubstantiated reports Unsubstantiated, and for Unable to Determine reports Closed No Finding.  For children being reported as household members where there are no allegations NCANDS reports No Alleged Maltreatment.');
INSERT INTO CAPS.NCANDS_ELEMENT_HELP (TXT_NCANDS_ELEMENT_SHORT,NBR_NCANDS_ORDER,TXT_NCANDS_ELEMENT,IND_NCANDS_ELEMENT_ACTIVE,TXT_NCANDS_ELEMENT_LABEL,CD_NCANDS_DATA_TYPE,TXT_NCANDS_CODES_TABLE,TXT_NCANDS_SOURCE_TEXT,TXT_NCANDS_ELEMENT_HELP_TEXT) VALUES ('NCANDS_28',28,'CHILD_MAL_2','Y','Maltreatment-2 Type','C','CMALTRTY','<a href="javascript:caseWatchNavigation(''navAllegationList'')">Allegation Detail</a>','<b>Maltreatment-2 Type</b><br>See definitions for maltreatment 1 above.<br>This field is blank for non-victims and children without a second maltreatment type');
